import { useEffect, useRef, useState } from "react";
import "./App.scss";

function App() {
  //todolist 프로젝트 생성
  //sass
  const [data, setData] = useState([]),
    [todoNum, setTodoNum] = useState(0),
    elInput = useRef(),
    elItems = useRef([]),
    count = useRef(0);

  const add = (e) => {
    //input값을 받아서와 data변수에 넣어주는 일
    e.preventDefault();
    let value = { num: ++count.current, todo: elInput.current.value };
    setData([...data, value]);
    elInput.current.value = "";
    elInput.current.focus();
  };

  /* 삭제버튼을 누른 li를 제외한 나머지 li를 data에 저장 */
  const remove = (n) => {
    let removeData = data.filter((obj) => obj.num !== n);
    setData(removeData);
  };
  const state = (e) => {
    e.target.classList.toggle("active");
    update();
  };

  const update = () => {
    let count = elItems.current.filter(
      (item) => item && item.className != "active"
    ).length;
    setTodoNum(count);
  };

  useEffect(update, [data]);

  return (
    <div className="App">
      <article>
        <h1>Todolist</h1>
        <p>할일 {todoNum}개 남음</p>
        <ul className="list">
          {data &&
            data.map((obj, key) => {
              return (
                <li
                  ref={(el) => (elItems.current[key] = el)}
                  onClick={state}
                  key={obj.num}
                >
                  {obj.todo}
                  <button onClick={() => remove(obj.num)}>삭제</button>
                </li>
              );
            })}
        </ul>
        <div className="write">
          <form onSubmit={add}>
            <input
              ref={elInput}
              type="text"
              name="w"
              placeholder="할 일을 입력하세요."
            />
            <input type="submit" value="저장" />
          </form>
        </div>
      </article>
    </div>
  );
}

export default App;
